INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(2, 1, '2017-02-01 12:55:48', '2017-02-01 12:55:48', 'This is an example page. It\'s different from a blog post because it will stay in one place and will show up in your site navigation (in most themes). Most people start with an About page that introduces them to potential site visitors. It might say something like this:\n\n<blockquote>Hi there! I\'m a bike messenger by day, aspiring actor by night, and this is my blog. I live in Los Angeles, have a great dog named Jack, and I like pi&#241;a coladas. (And gettin\' caught in the rain.)</blockquote>\n\n...or something like this:\n\n<blockquote>The XYZ Doohickey Company was founded in 1971, and has been providing quality doohickeys to the public ever since. Located in Gotham City, XYZ employs over 2,000 people and does all kinds of awesome things for the Gotham community.</blockquote>\n\nAs a new WordPress user, you should go to <a href=\"http://localhost/portifolio/wp-admin/\">your dashboard</a> to delete this page and create new pages for your content. Have fun!', 'Página de Exemplo', '', 'publish', 'open', 'open', '', 'pagina-exemplo', '', '', '2017-02-01 12:55:48', '2017-02-01 12:55:48', '', 0, 'http://localhost/portifolio/?page_id=2', 0, 'page', '', 0),
(4, 1, '2017-02-01 13:38:45', '2017-02-01 13:38:45', 'Site responsivo, desenvolvido em <strong>html5</strong>, <strong>bootstrap</strong>,  <strong>jquery</strong>, <strong>php</strong> e <strong>compass</strong>. O projeto possui várias animações e um efeito suave ao rolar da página. Foram usados mixins em <strong>compass</strong>/<strong>sass</strong> para adequação nos dispositvos móveis. O <strong>formulário</strong> foi tratado pelo <strong>phpmailer</strong> para o tratamento de envio de e-mails  <a href=\"http://aplicativosimul.com.br/novo\" target=\"_blank\">Clique aqui</a> para acessar o projeto', 'Aplicativo Simul', '', 'publish', 'open', 'open', '', 'aplicativo-simul', '', '', '2017-02-01 19:06:38', '2017-02-01 19:06:38', '', 0, 'http://localhost/portifolio/?p=4', 0, 'post', '', 0),
(5, 1, '2017-02-01 13:37:28', '2017-02-01 13:37:28', '', 'aplicativo_simul', '', 'inherit', 'open', 'open', '', 'aplicativo_simul', '', '', '2017-02-01 13:37:28', '2017-02-01 13:37:28', '', 4, 'http://localhost/portifolio/wp-content/uploads/2017/02/aplicativo_simul.jpg', 0, 'attachment', 'image/jpeg', 0),
(6, 1, '2017-02-01 13:38:45', '2017-02-01 13:38:45', 'Site Desenvolvido em HTML5, CSS, Jquery', 'Aplicativo Simul', '', 'inherit', 'open', 'open', '', '4-revision-v1', '', '', '2017-02-01 13:38:45', '2017-02-01 13:38:45', '', 4, 'http://localhost/portifolio/2017/02/01/4-revision-v1/', 0, 'revision', '', 0),
(8, 1, '2017-02-01 13:52:54', '2017-02-01 13:52:54', 'Site responsivo, desenvolvido em <strong>html5</strong>, <strong>bootstrap</strong>,  <strong>jquery</strong>, <strong>php</strong> e <strong>compass</strong>.  A propriedade <strong>flexbox</strong> foi responsável pelo alinhamento dos elementos de forma exata e mixins em <strong>compass</strong>/<strong>sass</strong> para adequação nos dispositvos móveis. O <strong>formulário</strong> foi tratado com elementos em <strong>html5</strong> dedicados a digitação de e-mail e telefone, facilitando o acesso através de um dispositivo móvel. O <strong>php</strong> foi responsável pela chamada das páginas e o <strong>phpmailer</strong> para o tratamento de envio de e-mails  <a href=\"http://vivavidaalimentosintegrais.com.br\" target=\"_blank\">Clique aqui</a> para acessar o projeto', 'Viva Vida Alimentos Integrais', '', 'publish', 'open', 'open', '', 'viva-vida-alimentos-integrais', '', '', '2017-02-01 18:55:19', '2017-02-01 18:55:19', '', 0, 'http://localhost/portifolio/?p=8', 0, 'post', '', 0),
